import React, { Component } from 'react';
import { Button, Form, FormGroup, FormControl, ControlLabel } from 'react-bootstrap';
import ReactDOM from 'react-dom';
import { getMaxListeners } from 'cluster';

const request = require('request');
const sha256 = require('sha256');

export default class Login extends Component {

    constructor (props){
        super (props);

        this.state = {
            email: "",
            username: "",
            password: "",
            isRegistered: true
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    validateForm() {
        return this.state.username.length > 0 && this.state.password.length > 0;
    }

    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const id = target.id;

        this.setState({
            [id]: value
        });
    }

    handleSubmit = (event) =>{
        //console.log('username:', this.state.username, 'password', this.state.password);
        this.authenticateExisting(this.state.username, this.state.password);
        //this.setState({password:""});
        event.preventDefault();
    }

    toggleIsRegistered = () => {
        this.setState({
            isRegistered: !this.state.isRegistered 
        });

    }

    authenticateExisting = (username,password) => {
        const options = {
            method: 'POST',
            url:'http://127.0.0.1:3838/auth_existing',
            headers:
            {
            "Content-Type": 'application/json'
            },
            body: {'username': username, 'password256': sha256(password)},
            json: true
        };

        request(options, (error, response, body) => {
            console.log("Successfully sent request!");
            if(error){
                console.error(new Error(error));
                return;
            }
            alert(body.message);
            console.log(body.message);

        });
        // const firebase = require('firebase');

        // const config = {
        //     apiKey: "AIzaSyAm_ibn9F-U-PWwkxSFDKBE3yqWudYyFPI",
        //     authDomain: "gitatransactions-labs.firebaseapp.com",
        //     databaseURL: "https://gita-labs.firebaseio.com",
        //     projectId: "gita-labs",
        //     storageBucket: "gita-labs.appspot.com",
        //     messagingSenderId: "714437387964"
        // };

        // firebase.initializeApp(config);
        // //Check local db for username => email
        // if(username==='vishalmenon'){
        //     var email = "vishalmenon.92@gmail.com";
        // }

        // firebase.auth().signInWithEmailAndPassword(email, password)
        // .then((msg)=>{
        //     alert("Successfully signed in!",msg);
        // })
        // .catch((error) => {
        //     // Handle Errors here.
        //     const errorCode = error.code;
        //     const errorMessage = error.message;
        //     // ...
        // });
        // // firebase.auth().createUserWithEmailAndPassword(email, password).catch(function(error) {
        // // // Handle Errors here.
        // // var errorCode = error.code;
        // // var errorMessage = error.message;
        // // // ...
        // // });

    }

    render(){
//use the Form.Group controlId as reference to this.state.username/password -> see handleChange()
        let userauth= this.state.isRegistered ?
        (
            <div className="Login">
            <Form onSubmit={this.handleSubmit}>
                <Form.Group controlId="username">
                    <Form.Label></Form.Label>
                    <Form.Control type='username' placeholder='Username' value={this.state.username} onChange={this.handleChange} />
                    <Form.Text> </Form.Text>
                </Form.Group>

                <Form.Group controlId="password"> 
                    <Form.Label></Form.Label>
                    <Form.Control type='password' placeholder='Password' value={this.state.password} onChange={this.handleChange} />
                    <Form.Text> </Form.Text>
                </Form.Group>

                <Form.Group controlId="formCheckbox">
                    <Form.Check type="checkbox" label="This checkbox does nothing as of now." />
                </Form.Group>

                <Button variant="primary" type="submit">
                    Login
                </Button>
                <hr />
                <Form.Text> Not registered?</Form.Text>
                <br />
                <Button variant="primary" type="submit" onClick={this.toggleIsRegistered}>
                    Sign-up
                </Button>
                <hr />

            </Form>
            </div>
        ) : 
        (
            <div className="Login">
            <Form>
                <Form.Group controlId="formEmail">
                    <Form.Label></Form.Label>
                    <Form.Control type='email' placeholder='Enter email' />
                    <Form.Text> </Form.Text>
                </Form.Group>

                <Form.Group controlId="formUsername">
                    <Form.Label></Form.Label>
                    <Form.Control type='username' placeholder='Username' />
                    <Form.Text> </Form.Text>
                </Form.Group>

                <Form.Group controlId="formPassword">
                    <Form.Label></Form.Label>
                    <Form.Control type='password' placeholder='Password' />
                    <Form.Text> </Form.Text>
                </Form.Group>

                <Form.Group controlId="formConfirmPassword">
                    <Form.Label></Form.Label>
                    <Form.Control type='password' placeholder='Confirm Password' />
                    <Form.Text> </Form.Text>
                </Form.Group>

                <Form.Group controlId="formCheckbox">
                    <Form.Check type="checkbox" label="This checkbox does nothing as of now." />
                </Form.Group>

                <Button variant="primary" type="submit">
                    Register
                </Button>
                <hr />
                <Form.Text> Already Registered?</Form.Text>
                <br />
                <Button variant="primary" type="submit" onClick={this.toggleIsRegistered}>
                   Login
                </Button>
                <hr />

            </Form>
            </div>
        );

        return (
            <div>
            {userauth}
            </div>
        );
    }
}
